mapper.update:
UPDATE REVIEW 
SET STATUS = 'N' 
WHERE REVIEW_ID = #{no}

UPDATE REVIEW
SET REVIEW_CONTENT = #{reviewContent} , REVIEW_RATING = #{reviewRating}
WHERE REVIEW_ID = #{reviewID}

UPDATE REVIEW
SET RESERVE = 'Y'
WHERE MT20ID = #{mt20id} AND ID = #{id}

UPDATE REVIEW
SET RESERVE = 'N'
WHERE MT20ID = #{mt20id} AND ID = #{id}

mapper.select:
  	 SELECT CASE WHEN EXISTS (
        SELECT 1
        FROM TICKET
        WHERE MT20ID = #{mt20id} AND ID = #{id}
		 ) THEN 'Y' ELSE 'N' END YN
	FROM DUAL
